import { Grid, IconButton, InputAdornment, TextField } from '@mui/material'
import InsertEmoticonIcon from '@mui/icons-material/InsertEmoticon'
import ImageIcon from '@mui/icons-material/Image'
import SendIcon from '@mui/icons-material/Send'
import React, { useCallback, useState } from 'react'
import '../../firebase'
import { getDatabase, push, ref, serverTimestamp, set } from 'firebase/database'
import { useSelector } from 'react-redux'
import data from '@emoji-mart/data'
import Picker from '@emoji-mart/react'

export default function ChatInput() {
  // 채널 정보 가져오기
  const { channel, user } = useSelector(state => state)

  //message 상태
  const [message, setMesssage] = useState('')

  //loading 상태
  const [loading, setLoading] = useState(false)

  //Emoji toggle
  const [showEmoji, setShowEmoji] = useState(false)

  //이모지
  const handleTogglePicker = useCallback(() => {
    setShowEmoji(show => !show)
  }, [])

  //message onChange로 저장
  const handleChange = useCallback(e => {
    setMesssage(e.target.value)
  }, [])

  // 메세지 생성
  const createMessage = useCallback(
    () => ({
      timestamp: serverTimestamp(),
      user: {
        id: user.currentUser.uid,
        name: user.currentUser.displayName,
        avatar: user.currentUser.photoURL,
      },
      content: message,
    }),
    [
      message,
      user.currentUser.uid,
      user.currentUser.displayName,
      user.currentUser.photoURL,
    ]
  )

  // 메시지 전송
  const clickSendMessage = useCallback(async () => {
    // 메세지가 없을경우 방어
    if (!message) return
    setLoading(true)

    // firebass realtimedatabase에 메세지 저장
    try {
      await set(
        push(ref(getDatabase(), 'messages/' + channel.currentChannel.id)),
        createMessage()
      )
      setLoading(false)
      setMesssage('')
    } catch (error) {
      console.error(error)
      setLoading(false)
    }
  }, [message, channel.currentChannel?.id, createMessage])

  //이모지 선택
  const handleSelectEmoji = e => {
    const sym = e.unified.split('-')
    console.log(sym)
  }

  return (
    <Grid container sx={{ p: '20px' }}>
      <Grid item xs={12} sx={{ position: 'relative' }}>
        {showEmoji && (
          <Picker
            data={data}
            onEmojiSelect={console.log}
            onSelect={handleSelectEmoji}
            className="emojipicker"
            title="이모지를 선택하세요."
            emoji="point_up"
            style={{ position: 'absolute', bottom: '80px' }}
            theme="light"
          />
        )}
        <TextField
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <IconButton onClick={handleTogglePicker}>
                  <InsertEmoticonIcon />
                </IconButton>
                <IconButton>
                  <ImageIcon />
                </IconButton>
              </InputAdornment>
            ),
            endAdornment: (
              <InputAdornment position="start">
                <IconButton disabled={loading} onClick={clickSendMessage}>
                  <SendIcon />
                </IconButton>
              </InputAdornment>
            ),
          }}
          autoComplete="off"
          label="메세지 입력"
          fullWidth
          value={message}
          onChange={handleChange}
        />
      </Grid>
    </Grid>
  )
}
