import {
  Avatar,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Input,
  Stack,
} from '@mui/material'
import React, { useCallback, useEffect, useRef, useState } from 'react'
import AvatarEditor from 'react-avatar-editor'
import '../../firebase'
import { useSelector } from 'react-redux'
import {
  getDownloadURL,
  getStorage,
  ref as refStorage,
  uploadBytes,
} from 'firebase/storage'
import { set } from 'firebase/database'
import { getAuth, updateProfile } from 'firebase/auth'

export default function ProfileModal({ open, handleClose }) {
  const { user } = useSelector(state => state)
  const [previewImage, setPreviewImage] = useState('')
  const [croppedImage, setCroppedImage] = useState('')
  const [uploadedCroppedImage, setUploadedCroppedImage] = useState('')
  const [blob, setBlob] = useState('')
  const avatarEditorRef = useRef(null)

  const handleChamge = useCallback(e => {
    const file = e.target.files[0] // 파일 가져오기
    if (!file) return // 파일 없을 경우 방어
    const reader = new FileReader()
    reader.readAsDataURL(file)
    reader.addEventListener('load', () => {
      setPreviewImage(reader.result)
    })
  }, [])

  const handleCropImage = useCallback(() => {
    avatarEditorRef.current.getImageScaledToCanvas().toBlob(blob => {
      const imageUrl = URL.createObjectURL(blob)
      setCroppedImage(imageUrl)
      setBlob(blob)
    })
  }, [])

  const uploadCroppedImage = useCallback(async () => {
    if (!user.currentUser?.uid) return
    const storageRef = refStorage(
      getStorage(),
      `avatasr/users/${user.currentUser.uid}`
    )

    const uploadTask = await uploadBytes(storageRef, blob)
    const downloadUrl = await getDownloadURL(uploadTask.ref)
    setUploadedCroppedImage(downloadUrl)

    set()
  }, [blob, user.currentUser?.uid])

  useEffect(() => {
    if (!uploadedCroppedImage) return
    async function changeAvatar() {
      const auth = getAuth()
      await updateProfile(auth.currentUser, {
        photoURL: uploadedCroppedImage,
      })

      const newData = { avatar: uploadedCroppedImage }
    }
    changeAvatar()
  }, [uploadedCroppedImage])

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>프로필 이미지 변경</DialogTitle>
      <DialogContent>
        <Stack direction="column" spacing={3}>
          <Input
            type="file"
            onChange={handleChamge}
            inputProps={{ accept: 'image/jpeg, image/jpg, image/png' }}
            label="변경할 프로필 이미지 선택"
          />
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {previewImage && (
              <AvatarEditor
                ref={avatarEditorRef}
                image={previewImage}
                width={120}
                height={120}
                border={50}
                scale={2}
                rotate={0}
                style={{ display: 'inline' }}
              />
            )}
            {croppedImage && (
              <img
                alt="cropped"
                style={{ marginLeft: '50px' }}
                width={100}
                height={100}
                src={croppedImage}
              />
            )}
          </div>
        </Stack>
      </DialogContent>
      <DialogActions>
        <Button>취소</Button>
        {previewImage && <Button onClick={handleCropImage}>이미지 Crop</Button>}
        {croppedImage && (
          <Button onClick={uploadCroppedImage}>프로필 이미지 저장</Button>
        )}
      </DialogActions>
    </Dialog>
  )
}
