import { Divider, Grid, List, Paper, Toolbar } from '@mui/material'
import React, { useEffect, useState } from 'react'
import ChatHeader from './ChatHeader'
import { useSelector } from 'react-redux'
import ChatInput from './ChatInput'
import ChatMessage from './ChatMessage'
import '../../firebase'
import { child, get, getDatabase, ref } from 'firebase/database'

export default function Chat() {
  // redux에서 채널명 가져오기
  const { channel, user } = useSelector(state => state)

  // 메세지
  const [messages, setMessages] = useState([])

  useEffect(() => {
    // 채널, 유저정보 없을 경우 방어
    if (!channel.currentChannel || !user.curentUser) return

    async function getMessages() {
      const snapShot = await get(
        child(ref(getDatabase()), 'messages/' + channel.currentChannel.id)
      )
      setMessages(snapShot.val() ? Object.values(snapShot.val()) : [])
    }
    getMessages()
    return () => {
      setMessages([])
    }
  }, [channel.currentChannel, user.curentUser])

  return (
    <>
      <Toolbar />
      <ChatHeader channelInfo={channel.currentChannel} />
      <Grid
        container
        component={Paper}
        variant="outlined"
        sx={{ mt: 3, position: 'relative' }}
      >
        <List
          sx={{
            height: 'calc(100vh - 350px)',
            overflow: 'scroll',
            width: '100%',
            position: 'relative',
          }}
        >
          {messages.map(message => (
            <ChatMessage
              key={message.timestamp}
              message={message}
              user={user}
            />
          ))}
        </List>
        <Divider />
        <ChatInput />
      </Grid>
    </>
  )
}
