import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Input,
} from '@mui/material'
import React, { useCallback, useState } from 'react'

export default function ImageModal({ open, handleClose }) {
  const [file, setFile] = useState(null)

  const onChangeAddFile = useCallback(event => {
    const addedfile = event.target.files[0]
    if (addedfile) setFile(addedfile)
  }, [])

  const uploadFile = () => {}

  const handleSendFile = useCallback(() => {
    uploadFile()
    handleClose()
    setFile(null)
  }, [handleClose])

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>이미지 보내기</DialogTitle>
      <DialogContent>
        <Input
          margin="dense"
          inputProps={{ accept: 'image/jpeg, image.jpg, image/png, image/gif' }}
          type="file"
          fullWidth
          variant="standard"
          onChange={onChangeAddFile}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>취소</Button>
        <Button onClick={handleSendFile}>전송</Button>
      </DialogActions>
    </Dialog>
  )
}
