import {
  Dialog,
  DialogContentText,
  DialogTitle,
  IconButton,
  List,
  ListItem,
  DialogContent,
  ListItemIcon,
  ListItemText,
  TextField,
  DialogActions,
  Button,
} from '@mui/material'
import React, { useCallback, useEffect, useState } from 'react'
import AddIcon from '@mui/icons-material/Add'
import ArrowDropDown from '@mui/icons-material/ArrowDropDown'
import '../firebase'
import {
  child,
  ref,
  push,
  getDatabase,
  update,
  onChildAdded,
} from 'firebase/database'

export default function ChannelMenu() {
  const [open, setOpen] = useState(false)
  const [channelName, setChannelName] = useState('')
  const [channelDescription, setChannelDescription] = useState('')
  const [channels, setChannels] = useState([])

  const handleClickOpen = () => {
    setOpen(true)
  }

  const handleClickClose = () => {
    setOpen(false)
  }

  useEffect(() => {
    const db = getDatabase()
    const unsubscribe = onChildAdded(ref(db, 'channels'), snapshot => {
      setChannels(channelArr => [...channelArr, snapshot.val()])
    })
    return () => {
      setChannels([])
      unsubscribe()
    }
  }, [])

  const handleSubmit = useCallback(async () => {
    const db = getDatabase()
    const key = push(child(ref(db)), 'chennels').key
    const newChannel = {
      id: key,
      name: channelName,
      details: channelDescription,
    }
    const updates = {}
    updates['/channels/' + key] = newChannel
    try {
      await update(ref(db), updates)
      setChannelName('')
      setChannelDescription('')
      handleClickClose()
    } catch (error) {
      console.error(error)
    }
  }, [channelDescription, channelName])

  return (
    <>
      <List sx={{ overflow: 'auto', width: 240, backgroundColor: '#4c3c4d' }}>
        <ListItem
          secondaryAction={
            <IconButton sx={{ color: '#9a939b' }} onClick={handleClickOpen}>
              <AddIcon />
            </IconButton>
          }
        >
          <ListItemIcon sx={{ color: '#9a939b' }}>
            <ArrowDropDown />
          </ListItemIcon>
          <ListItemText
            primary="채널"
            sx={{ wordBreak: 'break-all', color: '#9a939b' }}
          />
        </ListItem>
        {channels.map(channel => (
          <ListItem button selected></ListItem>
        ))}
      </List>
      <Dialog open={open} onClose={handleClickClose}>
        <DialogTitle>채널 추가</DialogTitle>
        <DialogContent>
          <DialogContentText>
            생성할 채널명과 설명을 입력해주세요.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="채널명"
            type="text"
            fullWidth
            variant="standard"
            onChange={e => setChannelName(e.target.value)}
          />
          <TextField
            margin="dense"
            label="설명"
            type="text"
            fullWidth
            variant="standard"
            onChange={e => setChannelDescription(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClickClose}>취소</Button>
          <Button onClick={handleSubmit}>생성</Button>
        </DialogActions>
      </Dialog>
    </>
  )
}
