import { Grid, IconButton, InputAdornment, TextField } from '@mui/material'
import InsertEmoticonIcon from '@mui/icons-material/InsertEmoticon'
import ImageIcon from '@mui/icons-material/Image'
import SendIcon from '@mui/icons-material/Send'
import React, { useCallback, useState } from 'react'
import '../../firebase'
import { getDatabase, push, ref, serverTimestamp, set } from 'firebase/database'
import { useSelector } from 'react-redux'

export default function ChatInput() {
  // 채널 정보 가져오기
  const { channel } = useSelector(state => state)

  //message 상태
  const [message, setMesssage] = useState('')

  //loading 상태
  const [loading, setLoading] = useState(false)

  //message set
  const handleChange = useCallback(e => {
    setMesssage(e.target.value)
  }, [])

  const createMessage = () => ({
    timestamp: serverTimestamp(),
    user: {
      id: user.currenUser.uid,
      name: user.currentUser.displayName,
      avatar: user.currentUser.photoURL,
    },
  })

  const clickSendMessage = useCallback(async () => {
    if (!message) return
    setLoading(true)
    try {
      await set(
        push(ref(getDatabase(), 'messages/' + channel.currentChannel.id)),
        {}
      )
    } catch (error) {
      console.error(error)
    }
  }, [message])

  return (
    <Grid container sx={{ p: '20px' }}>
      <Grid item xs={12} sx={{ position: 'relative' }}>
        <TextField
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <IconButton>
                  <InsertEmoticonIcon />
                </IconButton>
                <IconButton>
                  <ImageIcon />
                </IconButton>
              </InputAdornment>
            ),
            endAdornment: (
              <InputAdornment position="start">
                <IconButton disabled={loading} onClick={clickSendMessage}>
                  <SendIcon />
                </IconButton>
              </InputAdornment>
            ),
          }}
          autoComplete="off"
          label="메세지 입력"
          fullWidth
          value={message}
          onChange={handleChange}
        />
      </Grid>
    </Grid>
  )
}
